package app.kos.service.api;

import core.framework.api.http.HTTPStatus;
import core.framework.api.web.service.DELETE;
import core.framework.api.web.service.GET;
import core.framework.api.web.service.POST;
import core.framework.api.web.service.PUT;
import core.framework.api.web.service.Path;
import core.framework.api.web.service.PathParam;
import core.framework.api.web.service.ResponseStatus;

/**  
* @author stephen  
*/ 
public interface RunnerScannerWebService {
    @POST
    @ResponseStatus(HTTPStatus.CREATED)
    @Path("/runner-scanner/hdr/:hdrId/scanner")
    void create(@PathParam("hdrId") String hdrId, CreateScannerRequest request);

    @DELETE
    @Path("/runner-scanner/scanner/:id")
    void delete(@PathParam("id") String id);

    @PUT
    @Path("/runner-scanner/scanner/:id")
    void update(@PathParam("id") String id, UpdateScannerRequest request);

    @GET
    @Path("/runner-scanner/hdr/:hdrId/scanner")
    ListScannerResponse list(@PathParam("hdrId") String hdrId);
} 
```
